            <div class="type-project">
              <div class="tag">
                <i class="icon icon-user-check"></i>
                <span>{{ card.type }}</span>
              </div>
            </div>
            <div class="title">
              <span>{{ card.title }}</span>
            </div>
            <div class="description">
              <p>
                {{ card.description }}
              </p>
            </div>
            <div class="state">
              <span><span v-if="card.state">{{ card.state }} - </span><strong>{{ card.year }}</strong></span>
            </div>
            <div class="actions">
              <a v-if="card.link" :href="card.link" :style="`background-color: ${card.color[0]}`"
                style="width: 80px; font-size: 12px;" target="_blank" class="btn actionCard">
                Visitar
              </a>
              <a v-if="card.github" :href="card.github" :style="`background-color: ${card.color[0]}`" target="_blank"
                class="btn actionCard">
                <i class="icon icon-github"></i>
              </a>
              <a v-if="card.video" :href="card.video" :style="`background-color: ${card.color[0]}`" target="_blank"
                class="btn actionCard">
                <i class="icon icon-play"></i>
              </a>
            </div>
            <span class="title-tecnologies">Tecnologías</span>
            <div class="tecnologies">
              <span> {{ card.tecnologies }} </span>
            </div>
            <div class="logo">
              <span style="font-weight: 200; text-transform: capitalize">
                {{ card.logo }}
              </span>
            </div>
          </swiper-slide>
        </swiper>
